# Drawing vectors and matrices

draw_matrix <- function(M) {
  image(1:ncol(M), 1:nrow(M), t(M[nrow(M):1, ]),
        col = colorspace::diverging_hcl(
          15, h = c(180, 50), c = 80,
          l = c(20, 95), power = c(0.7, 1.3)),
        axes = FALSE,
        useRaster = TRUE)
}

